package com.sandeepbegudem.customer.payments.service.config;import io.swagger.v3.oas.models.OpenAPI;import io.swagger.v3.oas.models.info.Info;import io.swagger.v3.oas.models.info.Contact;import io.swagger.v3.oas.models.info.License;import io.swagger.v3.oas.models.servers.Server;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class OpenAPIConfig {    @Bean    public OpenAPI openApiInformation() {        Server localServer = new Server()                .url("http://localhost:6789")                .description("Localhost Server URL");        Contact contact = new Contact()                .email("sandeep.begudem@gmail.com")                .name("Sandeep Begudem");        Info info = new Info()                .contact(contact)                .description("Demo Customer Payments Backend Spring Boot Application")                .summary("Demo Customer Payments Backend Spring Boot Application")                .title("springboot3-jwt-authorization-junit5-mockito")                .version("V1.0.0")                .license(new License().name("Apache 2.0").url("http://springdoc.org"));        return new OpenAPI().info(info).addServersItem(localServer);    }}