package com.sandeepbegudem.customer.payments.service.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.List;@Data@Entitypublic class Customer {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    private String firstname;    private String lastname;    private int age;    private String address;    private String city;    private String state;    @OneToMany(cascade = CascadeType.ALL, orphanRemoval = true)    @JoinColumn(name = "customer_payments_fk")    private List<Payment> payments;    public Customer() {    }    public Customer(Integer id, String firstname, String lastname, int age, String address, String city, String state, List<Payment> payments) {        this.id = id;        this.firstname = firstname;        this.lastname = lastname;        this.age = age;        this.address = address;        this.city = city;        this.state = state;        this.payments = payments;    }}