package com.sandeepbegudem.customer.payments.service.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@AllArgsConstructor@NoArgsConstructor@Data@Entity@Table(name = "userinfo")public class UserInfo {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int id;    private String name;    private String password;    private String email;    private String roles;}