package com.sandeepbegudem.customer.payments.service.mapper;import com.sandeepbegudem.customer.payments.service.dto.PaymentDto;import com.sandeepbegudem.customer.payments.service.entity.Payment;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.Setter;import org.springframework.stereotype.Component;import java.util.Date;@AllArgsConstructor@Setter@Getter@Componentpublic class PaymentMapper {    public PaymentDto entityToDto(Payment paymentRequest){        PaymentDto paymentDto = new PaymentDto();        paymentDto.setPaymentId(paymentRequest.getPaymentId());        paymentDto.setPaymentMode(paymentRequest.getPaymentMode());        paymentDto.setPaymentTargetEntity(paymentRequest.getPaymentTargetEntity());        paymentDto.setPaymentAmount(paymentRequest.getPaymentAmount());        paymentDto.setPaymentDate(new Date());        paymentDto.setPaymentType(paymentRequest.getPaymentType());        paymentDto.setCustomer_payments_fk(paymentRequest.getCustomer_payments_fk());        return paymentDto;    }    public Payment dtoToEntity(PaymentDto paymentDto){        Payment payment = new Payment();        payment.setPaymentId(paymentDto.getPaymentId());        payment.setPaymentMode(paymentDto.getPaymentMode());        payment.setPaymentTargetEntity(paymentDto.getPaymentTargetEntity());        payment.setPaymentAmount(paymentDto.getPaymentAmount());        payment.setPaymentDate(new Date());        payment.setPaymentType(paymentDto.getPaymentType());        payment.setCustomer_payments_fk(paymentDto.getCustomer_payments_fk());        return payment;    }}